import Style from "./WeatherPreview.module.css";
import React from 'react';
import WeatherIcon from "./WeatherIcon";
import { fetchForecast } from "./Utils/Weather.js";

export default class WeatherPreview extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            weather: this.props.weather
		}
    }

    componentDidMount() {
        console.log(this.state.weather);
        if (!this.state.weather) {
            this.setState({ weather: fetchForecast(this.props.location.lat, this.props.location.lon, "minutely,hourly,daily,alerts") });
        }
	}

    render() {
        return <div className={Style.weatherPreview}>
            <WeatherIcon icon={this.state.weather?.current?.weather[0]?.icon} />
        </div>;
    }
}
