import Style from './LineChart.module.css';
import React from 'react';
import { Chart, registerables } from 'chart.js';
import ChartDataLabels from 'chartjs-plugin-datalabels';
Chart.register(...registerables);
Chart.register(ChartDataLabels);


export default class LineChart extends React.Component {
    constructor(props) {
        super(props)
        this.canvas = React.createRef();
    }

    canvasChart = null;

    componentDidMount() {
        this.canvasChart = new Chart(this.canvas.current, {
            type: 'line',
            data: {
                labels: this.props.data.map(d => d.x),
                datasets: [{
                    //labels: this.props.data.map(d => d.temp + "C"),
                    data: this.props.data.map(d => d.y),
                    fill: false,
                    borderColor: 'white',
                    borderDash: [5, 5],
                    borderWidth: 2,
                    pointRadius: 0,
                    tension: 0.1
                }]
            },
            options: {
                responsive: true,
                maintainAspectRatio: false,
                plugins: {
                    legend: {
                        display: false
                    },
                    datalabels: {
                        color: 'white',
                        align: 'top',
                        offset: 0,
                        formatter: (d, context) => {
                            if (context.dataIndex % 2 != 0) return null;
                            return d.toFixed(0) + "";
                        }
                    }
                },
                scales: {
                    x: {
                        grid: {
                            color: '#fff4',
                            drawBorder: false,
                            //drawOnChartArea: false,
                            tickColor: 'white'
                        },
                        ticks: {
                            color: 'white'
                        }
                    },
                    y: { display: false }
                }
            }
        });
    }

    componentWillUnmount() {
        this.canvasChart.destroy();
    }

    render() {
        return <canvas className={Style.chart} ref={this.canvas} />;
    }
}
