const apiKey = "f4bcb6a805ca765f7df85383dc7fdbab";

export default class Weather {
    fetchForecast = async (lat, lon, exclude) => {
        return await fetch(`https://api.openweathermap.org/data/3.0/onecall?exclued=${exclude}&lat=${lat}&lon=${lon}&appid=${apiKey}`).then(res => res.json());
    }
}
