import Style from "./AppBackground.module.css";
import React from 'react';
import BackgroundMap from "./BackgroundMap";

export default class AppBackground extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div className={Style.full}>
            <BackgroundMap location={this.props.location} />
            <BackgroundLandscape location={this.props.location} style={this.props.style} />
        </div>;
    }
}

class BackgroundLandscape extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <svg className={Style.svg}>
            <defs>
                <linearGradient id="grad1" x1="0%" y1="0%" x2="0%" y2="100%">
                    <stop offset="0%" style={{ stopColor: "rgb(255,255,255)", stopOpacity: 1 }} />
                    <stop offset="100%" style={{ stopColor: "rgb(0,0,0)", stopOpacity: 1 }} />
                </linearGradient>
                <filter id="f2" x="-50" y="-50" height="300" width="300">
                    <feGaussianBlur in="SourceGraphic" stdDeviation="30" />
                </filter>
            </defs>
            <svg x={this.props.style.sun.x + "%"} y={this.props.style.sun.y + "%"}>
                <defs>
                    <filter id="f1" x="-50" y="-50" height="300" width="300">
                        <feGaussianBlur in="SourceGraphic" result="blur" stdDeviation="30" />
                        <feBlend in="blur" in2="BackgroundImage" mode="screen" />
                    </filter>
                </defs>
                <mask id="moonMask">
                    <circle cx="100" cy="0" r="100" fill="black" />
                </mask>
                <g filter="url(#f1)" opacity={this.props.style.sun.opacity + "%"} transform="scale(1,1)" >
                    <circle cx="0" cy="0" r="100" fill={"hsl(" + this.props.style.sun.halo.hue + ", " + this.props.style.sun.halo.sat + "%, " + this.props.style.sun.halo.lum + "%)"} filter="url(#f1)" />
                    <circle cx="0" cy="0" r="100" strokeWidth="0" fill="white" mask="url(#moonMask)" />
                </g>
            </svg>
            <g filter="url(#f2)" opacity={this.props.style.cloud.opacity + "%"}>
                {(new Array(this.props.style.cloud.count)).fill("x").map((x, key) => { return <circle key={key} cx={(Math.random() * 120 - 10) + "%"} cy={(Math.random() * 50 + 10) + "%"} r={(Math.random() * 130 + 20) + "px"} fill="url(#grad1)" /> } ) }
            </g>
        </svg>;
    }
}
