import Style from "./ForecastContainer.module.css";
import React from 'react';
import WeatherIcon from './WeatherIcon';
import WeatherTemp from './WeatherTemp';
import { UNIXtoDateTime, getUTCTime } from './Utils/Weather.js';

export default class ForecastContainer extends React.Component {
    constructor(props) {
        super(props)
        console.log(props);
    }

    render() {
        return <div className={Style.forecastContainer}>
            <Forecast location={this.props.location} weather={this.props.weather} />
        </div>;
    }
}

class Forecast extends React.Component {
    constructor(props) {
        super(props)
        //console.log(this.props.weather);
    }

    render() {
        return <div className={Style.forecastPanel}>
            <div>
                left panel
            </div>
            <div className={Style.divider}></div>
            <div className={Style.locationName}>{this.props.location.name}</div>
            <div>
                <WeatherIcon className={Style.currentIcon} weatherCode={this.props.weather.current.weather[0].id} size="6em" time={this.props.weather.current.dt} timezone={this.props.weather.timezone_offset} />
                <div style={{ fontSize: "3em", textAlign: "center" }}><WeatherTemp temp={this.props.weather.current.temp} /></div>
            </div>
            <div className={Style.currentDetails}>
                <div title="cloud coverage">
                    <div className="diw-clouds"></div>
                    <div>{this.props.weather.current.clouds}%</div>
                </div>
                <div title="humidity">
                    <div className="diw-cloud-fog-2"></div>
                    <div>{this.props.weather.current.humidity}%</div>
                </div>
                <div title="pressure">
                    <div className="diw-thermometer-50"></div>
                    <div>{this.props.weather.current.pressure / 10} kPa</div>
                </div>
                <div title="sun rise">
                    <div className="diw-sun-rise"></div>
                    <div>{UNIXtoDateTime(this.props.weather.current.sunrise, this.props.weather.timezone_offset).toTimeString().split(":").slice(0,2).join(":")}</div>
                </div>
                <div title="sun set">
                    <div className="diw-sun-set"></div>
                    <div>{getUTCTime(UNIXtoDateTime(this.props.weather.current.sunset, this.props.weather.timezone_offset))}</div>
                </div>
                <div title="uv index">
                    <div className="diw-sun"></div>
                    <div>{this.props.weather.current.uvi}</div>
                </div>
                <div title="wind">
                    <div className="diw-wind"></div>
                    <div>{(this.props.weather.current.wind_speed * 3.6).toFixed(1)} kph <span className="diw-compass-north" style={{ transform: `rotate(${360 - this.props.weather.current.wind_deg}deg)`, fontSize: '1em', display: 'inline-block' }}></span></div>
                </div>
            </div>
        </div>;
    }
}
