import Style from "./ForecastDays.module.css";
import React from 'react';
import WeatherIcon from './WeatherIcon';
import WeatherTemp from './WeatherTemp';
import { getUTCDay } from './Utils/Weather.js';

export default class ForecastDays extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div className={Style.forecastDaysContainer}>
            {this.props.weather.weather.map((day, key) => <ForecastDay weather={day} key={key} />)}
        </div>;
    }
}

class ForecastDay extends React.Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div>
            <div>{getUTCDay(this.weather.dt)}</div>
        </div>;
    }
}
