import Style from "./App.module.css";
import React from 'react';
import Weather from './Utils/Weather.js';
import AppBackground from "./AppBackground";
import UI from "./UI";

export default class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            location: null,
            weather: null,
            storedLocations: JSON.parse(localStorage.getItem("WeatherApp_StoredLocations")) || [
                { name: "Cobourg, ON, Canada", lat: 43.967, lon: -78.167 },
                { name: "Innisfil, ON, Canada", lat: 44.3, lon: -79.5834 },
                { name: "Adelaide, SA, Australia", lat: -34.9275, lon: 138.6 },
            ],
        }
    }

    handleLocationChange = (data) => {
        this.setState({ weather: Weather.fetchForecast(data.lat, data.lon) });
    }

    render() {
        return <div >
            <AppBackground weather={this.state.weather} />
            <UI location={this.state.location} weather={this.state.weather} storedLocations={this.state.storedLocations} handleLocationChange={this.handleLocationChange} />
        </div>;
    }
}
